import { Container, Navbar, Nav } from 'react-bootstrap'
import { NavLink } from 'react-router-dom'
import { AppContext } from './AppContext.js'
import { useContext } from 'react'
import {Button} from 'react-bootstrap';
import { observer } from 'mobx-react-lite'


const NavBar = observer(() => {
    const { user, basket } = useContext(AppContext)
    return (
        <Navbar className='navlink' bg="black" variant="dark">
            <Container>
               <img className='logo' src={"./img/logo_light.svg"} />
                <Nav className="ml-auto">
                    <NavLink to="/home" className="nav-link">Главная</NavLink>

                    {user.isAuth ? (
                         <Link to="/user">
                         <button type="button">
                              Главная
                         </button>
                     </Link>
                    ) : (
                        <>
                            <NavLink to="/login" className="nav-link">Войти</NavLink>
                            <NavLink to="/signup" className="nav-link">Регистрация</NavLink>
                        </>
                    )}
                    {user.isAdmin && (
                        <a href="/admin" ><img style={{cursor: 'pointer', width: 60, height: 60}} src={"./img/pane.png"} /></a>
                    )}
                       <a href="/basket" ><img style={{cursor: 'pointer', width: 60, height: 60}} src={"./img/cartorange.png"} /></a>

                        {!!basket.count && <span>({basket.count})</span>}

                </Nav>
            </Container>
        </Navbar>
    )
})

export default NavBar
