import { Container, Button } from 'react-bootstrap'
import { useContext } from 'react'
import { AppContext } from '../components/AppContext.js'
import { Link, useNavigate } from 'react-router-dom'
import { logout } from '../http/userAPI.js'

const Admin = () => {
    const { user } = useContext(AppContext)
    const navigate = useNavigate()

    const handleLogout = (event) => {
        logout()
        user.logout()
        navigate('/login', {replace: true})
    }

    return (
        <div className='sales'>
        <Container className="d-flex flex-column">
            <h1 className='area' style= {{color: '#D16002'}} >Панель управления</h1>
            <div className="mt-4"> </div>

            <Button  
           variant = {"outline-light"} 
           className="col-6, mx-auto" 
           style={{width: 400, height:  100}}><Link className='link' to="/admin/orders">Заказы в магазине</Link></Button>
           <Button  
           variant = {"outline-light"} 
           className="col-6, mx-auto" 
           style={{width: 400, height:  100}}><Link className='link' to="/admin/categories">Категории каталога</Link></Button>
            <Button  
           variant = {"outline-light"} 
           className="col-6, mx-auto" 
           style={{width: 400, height:  100}}><Link className='link' to="/admin/brands">Бренды каталога</Link></Button>
            <Button  
           variant = {"outline-light"} 
           className="col-6, mx-auto" 
           style={{width: 400, height:  100}}><Link className='link' to="/admin/products">Товары каталога</Link></Button>

          <Button  
           variant = {"outline-light"} 
           className="col-6, mx-auto" 
           style={{width: 400, height:  100, fontSize: 30}} onClick={handleLogout}>Выйти</Button>
        </Container>
        </div>
    )
}

export default Admin
