import { Container, Row, Col, Button, Image, Spinner, Table } from 'react-bootstrap'
import { useEffect, useState, useContext } from 'react'
import { fetchOneProduct } from '../http/catalogAPI.js'
import { useParams } from 'react-router-dom'
import { append } from '../http/basketAPI.js'
import { AppContext } from '../components/AppContext.js'

const Product = () => {
    const { id } = useParams()
    const { basket } = useContext(AppContext)
    const [product, setProduct] = useState(null)


    useEffect(() => {
        fetchOneProduct(id).then(data => setProduct(data))

    }, [id])

    const handleClick = (productId) => {
        append(productId).then(data => {
            basket.products = data.products
        })
    }

    if (!product) {
        return <Spinner animation="border" />
    }

    return (
        <div className='bg2'>
        <Container maxWidth="lg">
        <div className='carditem'>
           <Row>
            <Col md={12}>
                        <Image className='cardimg' width={400} height={400} src={process.env.REACT_APP_IMG_URL + product.image} />
                </Col>
                </Row>
                <Row>
                <Col md={12}>
                    <h1>{product.name}</h1>
                    <h3>{product.price}.00 руб.</h3>
                    <p>Калорийность: {product.calories}</p>
                    <p>Категория: {product.category.name}</p>


                </Col>
            </Row>
            {!!product.props.length &&
                <Row>
                    <Col>
                        <h3>Описание</h3>

                                    {product.props.map(item => 
                                       <div key={item.id}>
                                            <h2 className='itemname'>{item.name}</h2>
                                            {item.value}
                                            <div className='white' style={{ padding: 10}}> Цена: {item.price} руб.</div>
                                            <div className='add-to-cart' > В корзину </div>
                                      </div>
                                    )}

                    </Col>
                </Row>
}
                </div>
        </Container>
        </div>
        );
    };

export default Product
